mapper.select:
SELECT STRT_NO
      ,END_NO
FROM   TBLCH_MULTI_SEQ
WHERE  SO_ID = #{soId}
AND    P_SEQ = #{pSeq}
AND    GUBUN = '1'

SELECT RATE_ITM_CD
      ,EXCL_SVC_RATE_ITM_TYP_CD
FROM   IFNBRM_DISC_EXCL
WHERE  SO_ID = #{soId}
AND    ACT_DT <= #{endDate} 
AND    INACT_DT >= #{startDate} 
ORDER  BY RATE_ITM_CD
