mapper.select:
SELECT
	SET_VAL
FROM TBLIV_BILL_CYCL_STP
WHERE BILL_YYMM = #{billYymm}
	AND BILL_CYCL = #{billCycl}
	AND SET_ITM_ID = #{setItmId}
	AND SO_ID = #{soId}

SELECT
	SET_VAL
FROM TBLIV_BILL_STP


		SO_ID = #{soId}


		AND SET_ITM_ID = #{setItmId}


		AND #{billYymm} BETWEEN EFT_BGN_YYMM AND EFT_END_YYMM


		AND BILL_YYMM = #{billYymm}
		AND BILL_CYCL = #{billCycl}


ORDER BY EFT_BGN_YYMM DESC
