	, SALE_TP
	, USG_CNT
	, USG_AMT
	, ADJ_PRV_BILL_AMT
	, ADJ_AMT
	, BILL_AMT
	, PAY_DUE_DT
	, RCPT_AMT
	, FULL_PAY_YN
	, ATRT_CORP_ID
	, ATRT_EMP_ID
	, WON_BILL_AMT
	, CRNCY_CD
	, EXRATE
	, EXRATE_APLY_DT
	, SALE_ITM_CD
	, PREPAY_OCC_SEQ_NO
	, PREPD_CTRT_ID
	, INPT_MENU_ID
	, REGR_ID
	, REG_DATE
	, CHGR_ID
	, CHG_DATE
	, DEBT_PROC_YN
)
VALUES
