	, #{billYymm}
	, #{billCycl}
	, #{billDt}
	, #{soId}
	, #{grpId}
	, #{pymAcntId}
	, #{custId}
	, #{ctrtId}
	, #{prodCd}
	, #{svcCd}
	, #{bizCl}
	, #{prodGrp}
	, #{svcGrp}
	, #{billMmTp}
	, #{saleTp}
	, 0
	, 0
	, 0
	, #{adjApplAmt}
	, #{adjApplAmt}
	, #{payDueDt}
	, 0
	, #{fullPayYn}
	, #{atrtCorpId}
	, #{atrtEmpId}
	, #{wonBillAmt}
	, #{crncyCd}
	, #{exrate}
	, #{exrateAplyDt}
	, #{saleItmCd}
	, #{prepayOccSeqNo}
	, #{prepdCtrtId}
	, #{inptMenuId}
	, #{regrId}
	, now()
	, #{chgrId}
	, now()
	, #{debtProcYn}
)

mapper.update:
UPDATE TBLIV_BILL_WRK
SET    BILL_AMT     = BILL_AMT     + #{adjApplAmt}
     , ADJ_AMT      = ADJ_AMT      + #{adjApplAmt}
     , WON_BILL_AMT = WON_BILL_AMT + #{adjApplAmt}
     , INPT_MENU_ID = #{inptMenuId}
     , CHGR_ID      = #{chgrId}
     , CHG_DATE     = #{chgDate}
WHERE  BILL_SEQ_NO  = #{billSeqNo}
AND    USE_YYMM     = #{useYymm}
AND    PROD_CMPS_ID = #{prodCmpsId}
AND    SVC_CMPS_ID  = #{svcCmpsId}
AND    CHRG_ITM_CD  = #{chrgItmCd}
AND    SO_ID        = #{soId}

